mapper.select:
SELECT
-- 级联表的字段 ,xiangmu.xiangmu_name as xiangmuName ,xiangmu.xiangmu_types as xiangmuTypes ,xiangmu.xiangmu_photo as xiangmuPhoto ,xiangmu.xiangmu_content as xiangmuContent ,xiangmu.xiangmu_delete as xiangmuDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_types as yonghuTypes ,yonghu.yonghu_delete as yonghuDelete
FROM xiangmu_commentback a left JOIN xiangmu xiangmu ON a.xiangmu_id = xiangmu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.xiangmu_id = #{params.xiangmuId}
and a.yonghu_id = #{params.yonghuId}
and a.xiangmu_commentback_text like CONCAT('%',#{params.xiangmuCommentbackText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.xiangmu_id IS NOT NULL
and xiangmu.xiangmu_name like CONCAT('%',#{params.xiangmuName},'%')
and xiangmu.xiangmu_types = #{params.xiangmuTypes}
and xiangmu.xiangmu_content like CONCAT('%',#{params.xiangmuContent},'%')
and xiangmu.xiangmu_delete >= #{params.xiangmuDeleteStart}
and xiangmu.xiangmu_delete <= #{params.xiangmuDeleteEnd}
and xiangmu.xiangmu_delete = #{params.xiangmuDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_types = #{params.yonghuTypes}
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
